import React, { useState } from "react";
import SignUpForm from "./SignUpForm";
import SignInForm from "./SignInForm";


const Log = () => {
    const [signUpModel, setSignUpMpdal] = useState(true);
    const [signInModel, setSignInMpdal] = useState(false);

    const handleModals = (e) => {
        if (e.target.id === 'register') {
            setSignInMpdal(false);
            setSignUpMpdal(true);
        } else if (e.target.id === 'login') {
            setSignUpMpdal(false);
            setSignInMpdal(true);
        }
    }

    return(
     <div className="connction-form">
        <div className="form-container">
            <ul>
                <li onClick={handleModals}>S'inscrire</li>
                <li onClick={handleModals}>Se connecter</li>
            </ul>
            {signUpModel && <SignUpForm />}
            {signInModel && <SignInForm />
            }

        </div>

     </div>
    )
}

export default Log;
